def ext_euclid(a, b):
     if b == 0: return 1, 0, a  #
     else: 
         x, y, q = ext_euclid(b, a % b) # q = gcd(a, b) = gcd(b, a%b)
         # x, y è¡¨ç¤ºåŸçº¿æ€§æ–¹ç¨‹çš„æ•´æ ¹
         # ç›´åˆ°é€’å½’åˆ°è¾¾åº•å±‚å‰éƒ½ä¸ä¼šè¢«å¤„ç†
         x, y = y, (x - (a // b) * y)
         # 
         return x, y, q&lt;/code&gt;&lt;/pre&gt;
